import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

const mapStateToProps = state => {
    return {
        adminAccessControlInfo: state.adminAccessControlInfoData.adminAccessControlInfo,
        name: state.userLogInData.name
    }
}

class NavButtons extends Component {
    render() {
        let accessGranted = false;
        for (let i = 0; i < this.props.adminAccessControlInfo.length; i++) {
            if (this.props.adminAccessControlInfo[i].name === this.props.name && this.props.adminAccessControlInfo[i].access === "Granted") {
                accessGranted = true;
                break;
            }
        }
        if (accessGranted === true) {
            return (
                <div style={{ textAlign: 'center' }}>
                    <Button size='small' style={{ fontSize: '2vw', marginRight: '0.5vw' }}
                        component={Link} to="/User/DocumentRepo">
                        User Console      </Button>
                    <Button size='small' style={{ fontSize: '2vw' }}
                        color='secondary'
                        component={Link} to="/Admin/DocumentRepo">
                        Admin Console      </Button>
                </div>
            );
        } else {
            return (
                <div style={{ textAlign: 'center' }}>
                    <Button size='small' style={{ fontSize: '2vw', marginRight: '0.5vw' }}
                        component={Link} to="/User/DocumentRepo">
                        User Console      </Button>
                </div>
            );
        }
    }
}

export default connect(mapStateToProps)(NavButtons)
