import React, { Component } from 'react';
import { connect } from 'react-redux';

const mapStateToProps = state => {
    return {
        tasks: state.editTasks.tasksVar
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
    }
}

class MainDisplay extends Component {
    constructor(){
        super();
        this.state={taskListLength: 0};
    }

    componentDidUpdate(){
        console.log(this.props.tasks.length)
        this.setState({taskListLength:this.props.tasks.length})
    }

    render() {
        const taskList = [];
        for (let i = 0; i < this.state.taskListLength; i++) {
            taskList.push("Hello")
        }

        return (
            <div>
                {taskList}
            </div>
        )
    }
}

export default connect(mapStateToProps)(MainDisplay)
