import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import { connect } from 'react-redux';
import {
    setRowSelectedUserDocRepo,
    setUserEditDocRepoStatus,
    changeUserEditDataFieldSelected,
    changeUserEditDataValueSelected,
    deleteData,
    addActivity
} from '../../actions';
import {getCurrentDateTime} from '../../globalFunctions'

const mapStateToProps = state => {
    return {
        selectedRowUserDocRepo: state.selectedRowUserDocRepoData.selectedRowUserDocRepo,
        accessControlInfo: state.adminAccessControlInfoData.adminAccessControlInfo,
        name: state.userLogInData.name,
        info: state.documentRepoInfoData.documentRepoInfoDataVar,
        dept: state.userLogInData.dept
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        setRowSelectedUserDocRepo: (key) => dispatch(setRowSelectedUserDocRepo(key)),
        setUserEditDocRepoStatus: (bool) => dispatch(setUserEditDocRepoStatus(bool)),
        changeUserEditDataFieldSelected: (text) => dispatch(changeUserEditDataFieldSelected(text)),
        changeUserEditDataValueSelected: (text) => dispatch(changeUserEditDataValueSelected(text)),
        deleteData: (key) => dispatch(deleteData(key)),
        addActivity: (data) => dispatch(addActivity(data))
    }
}

class DeleteButton extends Component {

    handleClick = () => {
        if (this.props.selectedRowUserDocRepo !== '') {
            if (window.confirm('Are you sure you want to delete this?')) {

                this.props.addActivity({
                    activity: 'Deleted Document: ' + this.props.info[this.props.selectedRowUserDocRepo].code,
                    user: this.props.name,
                    date: getCurrentDateTime(),
                    division: this.props.dept,
                    ip: '111.111.111'
                })
                this.props.setUserEditDocRepoStatus(false)
                this.props.changeUserEditDataFieldSelected('')
                this.props.changeUserEditDataValueSelected('')
                this.props.setRowSelectedUserDocRepo('')
                this.props.deleteData(this.props.selectedRowUserDocRepo)
            }
        } else {
            window.alert('Please select the data you wish to delete.')
        }
    }

    render() {
        return (
            <div>
                <Button style={{ paddingRight: '1rem', fontSize: '0.6vw' }}
                    variant="outlined"
                    onClick={() => { this.handleClick() }}>
                    Delete
                        </Button>
            </div>
        )
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(DeleteButton);
