import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import { connect } from 'react-redux';
import {
    setRowSelectedUserDocRepo,
    setUserEditDocRepoStatus,
    changeUserEditDataFieldSelected,
    changeUserEditDataValueSelected
} from '../../actions';

const mapStateToProps = state => {
    return {
        userEditDocumentRepoStatus: state.userEditDocumentRepoStatusData.userEditDocumentRepoStatus,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        setRowSelectedUserDocRepo: (key) => dispatch(setRowSelectedUserDocRepo(key)),
        setUserEditDocRepoStatus: (bool) => dispatch(setUserEditDocRepoStatus(bool)),
        changeUserEditDataFieldSelected: (text) => dispatch(changeUserEditDataFieldSelected(text)),
        changeUserEditDataValueSelected: (text) => dispatch(changeUserEditDataValueSelected(text))
    }
}

class EditButton extends Component {
    button_color = function (status) {
        switch (status) {
            case true:
                return 'secondary'
            case false:
                return 'black'
            default:
                return 'black'
        }
    }
    handleClick = () => {
        if (this.props.userEditDocumentRepoStatus === true) {
            this.props.setUserEditDocRepoStatus(false)
            this.props.changeUserEditDataFieldSelected('')
            this.props.changeUserEditDataValueSelected('')
            this.props.setRowSelectedUserDocRepo('')
        }
        else {
            this.props.setUserEditDocRepoStatus(true)
        }
    }
    render() {
        return (
            <div style={{ paddingRight: '1rem' }}>
                <Button style={{ paddingRight: '1rem', fontSize: '0.6vw' }}
                    color={this.button_color(this.props.userEditDocumentRepoStatus)}
                    variant="outlined"
                    onClick={() => { this.handleClick() }}>
                    Edit
                        </Button>
            </div>
        )
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(EditButton);
