import {
    ADD_NEW_TASK
} from './constants';

export const addNewTask = (form) => ({
    type: ADD_NEW_TASK,
    payload: form
})

export const testStateChange = (val) => ({
    type: TEST_STATE_CHANGE,
    payload: val
})
