import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Circle } from 'react-shapes';
import {
    displayTaskInfoFlip,
    displayANTBoolFlip
} from '../actions'
import Button from '@material-ui/core/Button';

const mapStateToProps = state => {
    return {
        tasks: state.editTasks.tasksVar,
        tasksLength: state.editTasks.tasksLength
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        displayTaskInfoFlip: (bool) => dispatch(displayTaskInfoFlip(bool)),
        displayANTBoolFlip: (bool) => dispatch(displayTaskInfoFlip(bool))
    }
}

class Visualisation extends Component {

    render() {

        const tasksList = [];

        for (let i = 0; i < this.props.tasksLength; i++) {
            { console.log(this.props.tasks[i].subtasks.length * 40) }
            tasksList.push(
                <div>
                    <Button className='AddTaskButton' onClick={() => { this.props.displayANTBoolFlip(true) }}>{this.props.tasks[i].name}</Button><br/>
                    <Circle r={this.props.tasks[i].subtasks.length * 40} fill={{ color: '#2409ba' }} /><br />
                </div>
            )
        }

        return (
            <div>
                {tasksList}
            </div>
        )
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Visualisation)
