import React, { Component } from 'react';
import { connect } from 'react-redux';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import {activityColor} from '../../globalFunctions'

const mapStateToProps = state => {
  return {
    searchfield: state.userActivityLogSearchData.userActivityLogSearchField,
    sort: state.userActivityLogSortData.userActivityLogSort,
    info: state.adminAuditLogInfoData.adminAuditLogInfo
  }
}

class SimpleTable extends Component {
  render() {
    let {
      searchfield,
      sort,
      info
    } = this.props

    let filteredInfo = info.filter(info => {
      return (
        info.date.includes(searchfield) ||
        info.activity.toLowerCase().includes(searchfield.toLowerCase()) ||
        info.user.toLowerCase().includes(searchfield.toLowerCase()));
    })

    switch (sort) {
      case "date":
        filteredInfo.sort(function (a, b) {
          var dateA = new Date(a.date), dateB = new Date(b.date)
          return dateA - dateB;
        })
        break;
      case "activity":
        filteredInfo.sort(function (a, b) {
          var activityA = a.activity.toLowerCase(), activityB = b.activity.toLowerCase()
          if (activityA < activityB) {
            return -1
          }
          if (activityA < activityB) {
            return 1
          }
          return 0
        })
        break;
      case "user":
        filteredInfo.sort(function (a, b) {
          var userA = a.user.toLowerCase(), userB = b.user.toLowerCase()
          if (userA < userB) {
            return -1
          }
          if (userB < userA) {
            return 1
          }
          return 0
        })
        break;
      default:
        break;
    }

    return (
      <Paper style={{ marginTop: '0px' }}>
        <Table>
          <TableHead >
            <TableRow style={{ height: '2.5vw' }}>
              <TableCell style={{ fontSize: '1.5vw' }} >Date</TableCell>
              <TableCell style={{ fontSize: '1.5vw' }} align="right">Activity</TableCell>
              <TableCell style={{ fontSize: '1.5vw' }} align="right">User</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {filteredInfo.map(row => (
              <TableRow >
                <TableCell component="th" scope="row" style={{ fontSize: '0.8vw' }}>
                  {row.date}
                </TableCell>
                <TableCell style={{ fontSize: '0.8vw', color: activityColor(row.activity) }} align="right">{row.activity}</TableCell>
                <TableCell style={{ fontSize: '0.8vw' }} align="right">{row.user}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    );
  }
}

export default connect(mapStateToProps)(SimpleTable);
