    }
}

class MainDisplay extends Component {

    constructor(){
        super();
        this.updateTaskListLength = this.updateTaskListLength.bind(this);
        this.state = ({taskListLength:0});
      }  

      componentDidMount() {
        this.updateTaskListLength();
      }

      componentDidUpdate(prevProps) {
        if(this.props.tasks!=prevProps.tasks)
        {
          this.updateTaskListLength();
        }
      } 

      updateTaskListLength() {
        this.setState({taskListLength:this.props.tasks.length})
      }

    render() {
        const taskList = [];
        for (let i = 0; i < this.state.taskListLength; i++) {
            taskList.push("Hello")
        }

        return (
            <div>
                {taskList}
            </div>
        )
    }
}

export default connect(mapStateToProps)(MainDisplay)
