import React, { Component } from 'react';
import { connect } from 'react-redux';
import Button from '@material-ui/core/Button';
import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles'
import { green } from '@material-ui/core/colors';
import { resetRowSelected } from '../../actions';

const mapStateToProps = state => {
    return {
        adminAccessControlSelectedRows: state.adminAccessControlSelectedRowsData.adminAccessControlSelectedRows,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        resetRowSelected: (key) => dispatch(resetRowSelected(key))
    }
}

const greenTheme = createMuiTheme({ palette: { primary: green } })

class AccessButtons extends Component {

    handleGrant = () => {
        for (var i = 0; i < this.props.adminAccessControlSelectedRows.length; i++) {
            this.props.info.find(a => a.user === this.props.adminAccessControlSelectedRows[i]).access = 'Granted'
            this.props.resetRowSelected()
        }
    }

    handleRevoke = () => {
        for (var i = 0; i < this.props.adminAccessControlSelectedRows.length; i++) {
            this.props.info.find(a => a.user === this.props.adminAccessControlSelectedRows[i]).access = 'Revoked'
            this.props.resetRowSelected()
        }
    }

    render() {
        return (
            <div>
                <MuiThemeProvider theme={greenTheme}>
                    <Button size='small' style={{ fontSize: '0.6vw' }} color='primary' variant="outlined" onClick={() => { this.handleGrant() }}>
                        Grant Access
                    </Button>
                </MuiThemeProvider>
                <Button size='small' style={{ fontSize: '0.6vw' }} color='secondary' variant="outlined" onClick={() => { this.handleRevoke() }}>
                    Revoke Access
                </Button>
            </div>
        );
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(AccessButtons)
