import React, { Component } from 'react';
import { connect } from 'react-redux';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const mapStateToProps = state => {
    return {
        tasks: state.editTasks.tasksVar,
        tasksLength: state.editTasks.tasksLength,
        selectedTask: state.setTaskSelected.taskSelectedVar
    }
}

class TaskDetails extends Component {

    render() {

        // const tasksList = [];

        // for (let i = 0; i < this.props.tasksLength; i++) {
        //     tasksList.push(
        //         <div>
        //             <Button className='taskNameButton' onClick={() => { this.taskNameClicked(i) }}>{this.props.tasks[i].name}</Button><br/>
        //         </div>
        //     )
        // }

        return (
            <div className='taskDetailsInfo'>
                <h1>{this.props.tasks[this.props.selectedTask].name}</h1>
                <h2>Due Date: {this.props.tasks[this.props.selectedTask].date}</h2>
                {this.props.tasks[this.props.selectedTask].subtasks.map(function (subtask) {
                    return <li >{subtask}</li>
                })}
                {console.log(this.props.tasks[this.props.selectedTask].subtasks)}
            </div>
        )
    }
}

export default connect(mapStateToProps)(TaskDetails)
