import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import {
    setSearchField,
    setSort,
    setStatus,
    setRowSelectedAdminDocRepo,
    setAdminEditDocRepoStatus,
    editData,
    changeAdminEditDataFieldSelected,
    changeAdminEditDataValueSelected
} from '../../actions';


const mapDispatchToProps = (dispatch) => {
    return {
        onSearchChange: (event) => dispatch(setSearchField(event.target.value)),
        setSort: (event) => dispatch(setSort(event.target.value)),
        setStatus: (text) => dispatch(setStatus(text)),
        setRowSelectedAdminDocRepo: (key) => dispatch(setRowSelectedAdminDocRepo(key)),
        setAdminEditDocRepoStatus: (bool) => dispatch(setAdminEditDocRepoStatus(bool)),
        editData: (data) => dispatch(editData(data)),
        changeAdminEditDataFieldSelected: (text) => dispatch(changeAdminEditDataFieldSelected(text)),
        changeAdminEditDataValueSelected: (text) => dispatch(changeAdminEditDataValueSelected(text))
    }
}

class UploadButton extends Component {

    handleClick = () => {
        this.props.setAdminEditDocRepoStatus(false)
        this.props.changeAdminEditDataFieldSelected('')
        this.props.changeAdminEditDataValueSelected('')
        this.props.setRowSelectedAdminDocRepo('')
    }

    render() {
        return (
            <div style = {{paddingRight: '1rem'}}>
                <Button size='small'
                    variant="outlined"
                    style={{ fontSize: '0.6vw' }}
                    component={Link} to="/Admin/AdminUpload"
                    onClick={() => { this.handleClick() }}>
                    Upload      </Button>
            </div>
        );
    }
}

export default connect(null,mapDispatchToProps)(UploadButton)
