import React, { Component } from 'react';
import { connect } from 'react-redux';
// import { Circle } from 'react-shapes';
import Konva from 'konva';
import { Stage, Layer, Rect, Text, Circle, Line } from 'react-konva';


const mapStateToProps = state => {
    return {
        tasks: state.editTasks.tasksVar,
        tasksLength: state.editTasks.tasksLength
    }
}

class Visualisation extends Component {

    render() {

        const tasksList = [];

        for (let i = 0; i < this.props.tasksLength; i++) {
            tasksList.push(
                <div>
                    <Circle x={100} y={100} radius={this.props.tasks[i].subtasks.length * 40} fill="green" />
                </div>
            )
        }

        return (
            <div>
                {tasksList}
                <Circle x={200} y={100} radius={50} fill="green" />
            </div>
        )
    }
}

export default connect(mapStateToProps)(Visualisation)
