import React, { Component } from 'react';
import { connect } from 'react-redux';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const mapStateToProps = state => {
    return {
        typeOptionsVar: state.typeOptionsData.typeOptionsVar,
        marketOptionsVar: state.marketOptionsData.marketOptionsVar,
        languageOptionsVar: state.languageOptionsData.languageOptionsVar
    }
}

class DataTable extends Component {

    handleChange = (key) => {
        if (this.props.selectedActionRow !== 0) {
            if (this.props.selectedEditorRow !== key) {
                this.props.setEditorRowSelected(key)
            } else {
                this.props.setEditorRowSelected('')
            }
        }
    }

    bgColor = (key) => {
        if (this.props.selectedActionRow === 0) {
            return 'white';
        }
        if (this.props.selectedEditorRow === key) {
            return '#d1d1d1';
        } else {
            return 'white';
        }
    }

    render() {
        const correctInfo = (selectedField) => {
            switch (selectedField) {
                case 0:
                    return this.props.marketOptionsVar;
                case 1:
                    return this.props.typeOptionsVar;
                case 2:
                    return this.props.languageOptionsVar;
                default:
                    return this.props.marketOptionsVar;
            }
        }

        const cellFontSize = (selectedField) => {
            if (selectedField === this.props.marketOptionsVar) {
                var num_market = 16 / (selectedField.length)
                return num_market.toString() + 'rem';
            } else {
                var num = 13 / (selectedField.length)
                return num.toString() + 'rem';
            }
        }
        return (
            <div>
                <div style={{ overflow: "auto" }}>
                    <Paper>
                        <div style={{ overflow: "auto", height: '200px' }}>
                            <Table className='border' style={{ tableLayout: "fixed" }}>
                                <TableBody>
                                    <TableRow style={{ backgroundColor: '#800000' }}>
                                        <TableCell style={{ fontSize: '7rem', color: 'white' }} align="center" component="th" scope="row">
                                            EDIT
                                        </TableCell>
                                    </TableRow>
                                </TableBody>
                            </Table>
                        </div>
                        <div style={{ overflow: "auto", height: '572px' }}>
                            <Table className='border' style={{ tableLayout: "fixed" }}>
                                <TableBody>
                                    {correctInfo(this.props.selectedFieldRow).map(row => (
                                        <TableRow key={row.key} onClick={event => this.handleChange(row.key)} style={{ backgroundColor: this.bgColor(row.key) }}>
                                            <TableCell style={{ fontSize: (cellFontSize(correctInfo(this.props.selectedFieldRow))) }} align="center" component="th" scope="row">
                                                {row.value}
                                            </TableCell>
                                            <TableCell style={{ fontSize: (cellFontSize(correctInfo(this.props.selectedFieldRow))) }} align="center" component="th" scope="row">
                                                {row.text}
                                            </TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </div>
                    </Paper>
                </div>
            </div>
        );
    }
}

export default connect(mapStateToProps)(DataTable);
