import React, { Component } from "react";
import Drawer from "@material-ui/core/Drawer";
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Typography from '@material-ui/core/Typography'
import { Link } from 'react-router-dom';
import LogOut from './LogOut'

class NavDrawer extends Component {
    render() {
        if (this.props.console === 'user') {
            return (
                <Drawer
                    anchor="left"
                    open={this.props.drawerOpened}
                    onClose={this.props.toggleDrawer(false)}
                >
                    <div
                        onClick={this.props.toggleDrawer(false)}
                        onKeyDown={this.props.toggleDrawer(false)}
                    >
                        <div>
                            <List>
                                <ListItem button component={Link} to="/">
                                    <ListItemText
                                        disableTypography
                                        primary={<Typography type="body2" style={{ fontSize: '1vw' }}>Home</Typography>}
                                    />
                                </ListItem>
                                <ListItem button component={Link} to="/User/DocumentRepo">
                                    <ListItemText
                                        disableTypography
                                        primary={<Typography type="body2" style={{ fontSize: '1vw' }}>Document Repository</Typography>}
                                    />              </ListItem>
                                <ListItem button component={Link} to="/User/ActivityLog">
                                    <ListItemText
                                        disableTypography
                                        primary={<Typography type="body2" style={{ fontSize: '1vw' }}>Activity Log</Typography>}
                                    />              </ListItem>
                                <ListItem button component={Link} to="/User/UserUpload">
                                    <ListItemText
                                        disableTypography
                                        primary={<Typography type="body2" style={{ fontSize: '1vw' }}>User Upload</Typography>}
                                    />              </ListItem>
                                <LogOut />
                            </List>
                        </div>
                    </div>
                </Drawer >
            );
        }
        else if (this.props.console === 'admin') {
            return (
                <Drawer
                    anchor="left"
                    open={this.props.drawerOpened}
                    onClose={this.props.toggleDrawer(false)}
                >
                    <div
                        onClick={this.props.toggleDrawer(false)}
                        onKeyDown={this.props.toggleDrawer(false)}
                    >
                        <div>
                            <List>
                                <ListItem button component={Link} to="/">
                                    <ListItemText
                                        disableTypography
                                        primary={<Typography type="body2" style={{ fontSize: '1vw' }}>Home</Typography>}
                                    />              </ListItem>
                                <ListItem button component={Link} to="/Admin/DocumentRepo">
                                    <ListItemText
                                        disableTypography
                                        primary={<Typography type="body2" style={{ fontSize: '1vw' }}>Document Repository</Typography>}
                                    />              </ListItem>
                                <ListItem button component={Link} to="/Admin/AccessControl">
                                    <ListItemText
                                        disableTypography
                                        primary={<Typography type="body2" style={{ fontSize: '1vw' }}>Access Control</Typography>}
                                    />              </ListItem>
                                <ListItem button component={Link} to="/Admin/AuditLog">
                                    <ListItemText
                                        disableTypography
                                        primary={<Typography type="body2" style={{ fontSize: '1vw' }}>Audit Log</Typography>}
                                    />              </ListItem>
                                <ListItem button component={Link} to="/Admin/AdminUpload">
                                    <ListItemText
                                        disableTypography
                                        primary={<Typography type="body2" style={{ fontSize: '1vw' }}>Admin Upload</Typography>}
                                    />              </ListItem>
                                <LogOut />
                            </List>
                        </div>
                    </div>
                </Drawer >
            );
        }
        else {
            return null;
        }
    }
}

export default NavDrawer
