import {
    ADD_NEW_TASK,
    DISPLAY_ANT_BOOL_FLIP,
    DISPLAY_TASK_INFO_FLIP
} from './constants';

export const addNewTask = (form) => ({
    type: ADD_NEW_TASK,
    payload: form
})

export const displayANTBoolFlip = (bool) => ({
    type: DISPLAY_ANT_BOOL_FLIP,
    payload: bool
})

export const displayTaskInfoFlip = (bool) => ({
    type: DISPLAY_TASK_INFO_FLIP,
    payload: bool
})
