import {
    ADD_NEW_TASK,
    DISPLAY_ANT_BOOL_TRUE
} from './constants';

export const addNewTask = (form) => ({
    type: ADD_NEW_TASK,
    payload: form
})

export const displayANTBoolTrue = (bool) => ({
    type: DISPLAY_ANT_BOOL_TRUE,
    payload: bool
})

export const displayANTBoolFalse = (bool) => ({
    type: DISPLAY_ANT_BOOL_FALSE,
    payload: bool
})
