import React, { Component } from 'react';
import { connect } from 'react-redux';
import NavBar from '../UniversalComponents/NavBar'
import Button from '@material-ui/core/Button';
import {
    addNewTask
} from '../actions'


const mapStateToProps = state => {
    return {
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        addNewTask: (form) => dispatch(addNewTask(form))
    }
}

class AddTask extends Component {

    constructor() {
        super()
        this.state = {

            formControls: {
                name: {
                    value: ''
                },
                date: {
                    value: ''
                },
                difficulty: {
                    value: ''
                }
            }
        }

    }

    changeHandler = event => {

        const name = event.target.name;
        const value = event.target.value;

        this.setState({
            formControls: {
                ...this.state.formControls,
                [name]: {
                    ...this.state.formControls[name],
                    value
                }
            }
        });

    }

    handleSubmit = () => {

        this.setState({
            formControls: {
                name: {
                    value: ''
                },
                date: {
                    value: ''
                },
                difficulty: {
                    value: ''
                }
            }
        })

        this.props.addNewTask({
            name: this.state.formControls.name.value,
            date: this.state.formControls.date.value,
            difficulty: this.state.formControls.difficulty.value
        })
    }


    render() {
        return (
            <form>

                <NavBar />

                <input type="text"
                    name="name"
                    className = 'addTaskInputName'
                    value={this.state.formControls.name.value}
                    onChange={this.changeHandler}
                />

                <input type="date"
                    name="date"
                    className = 'addTaskInputDate'
                    value={this.state.formControls.date.value}
                    onChange={this.changeHandler}
                />

                <select name="difficulty" 
                    value={this.state.formControls.difficulty.value}
                    className = 'addTaskInputDifficulty'
                    onChange={this.changeHandler}>
                    <option value="Easy">Easy</option>
                    <option value="Medium">Medium</option>
                    <option value="Difficult">Difficult</option>
                </select>

                <Button size='small' onClick={() => { this.props.handleSubmit()}}>
                        Submit
                    </Button>

            </form>
        );
    }

}

export default connect(mapStateToProps,mapDispatchToProps)(AddTask)
