import React, { Component } from 'react';
import { connect } from 'react-redux';
import { setDept } from '../../actions'

const mapDispatchToProps = (dispatch) => {
    return {
        setDept: (text) => dispatch(setDept(text)),
    }
}

class SelectFilter extends Component {

    constructor(props) {
        super(props)
        this.state = {
            result: ''
        };
    }

    handleChange = (event) => {
        this.props.setDept(event.target.value)
        this.setState({
            result: event.target.value
        })

    }

    render() {
        return (
            <div>
                <select style={{ fontSize: '0.5vw' }} onChange={this.handleChange}>
                    <option value="" disabled selected>Search department:</option>
                    <option value="none">None</option>
                    <option value="aaaaaa">aaaaaa</option>
                    <option value="bbbbbb">bbbbbb</option>
                    <option value="cccccc">cccccc</option>
                    <option value="dddddd">dddddd</option>
                    <option value="eeeeee">eeeeee</option>
                    <option value="ffffff">ffffff</option>
                </select>
            </div>
        )
    }
}

export default connect(null,mapDispatchToProps)(SelectFilter)
