import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import { connect } from 'react-redux';
import {
    setRowSelectedAdminDocRepo,
    setAdminEditDocRepoStatus,
    editData,
    changeAdminEditDataFieldSelected,
    changeAdminEditDataValueSelected,
    addActivity
} from '../../actions';
import { getCurrentDateTime, isValidCode, isValidDate } from '../../globalFunctions'

const mapStateToProps = state => {
    return {
        info: state.documentRepoInfoData.documentRepoInfoDataVar,
        adminEditDocumentRepoStatus: state.adminEditDocumentRepoStatusData.adminEditDocumentRepoStatus,
        selectedRowAdminDocRepo: state.selectedRowAdminDocRepoData.selectedRowAdminDocRepo,
        typeOptions: state.typeOptionsData.typeOptionsVar,
        marketOptions: state.marketOptionsData.marketOptionsVar,
        languageOptions: state.languageOptionsData.languageOptionsVar,
        adminEditField: state.adminEditFieldData.adminEditField,
        adminEditValue: state.adminEditValueData.adminEditValue,
        accessControlInfo: state.adminAccessControlInfoData.adminAccessControlInfo,
        name: state.userLogInData.name,
        dept: state.userLogInData.dept
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        setRowSelectedAdminDocRepo: (key) => dispatch(setRowSelectedAdminDocRepo(key)),
        setAdminEditDocRepoStatus: (bool) => dispatch(setAdminEditDocRepoStatus(bool)),
        editData: (data) => dispatch(editData(data)),
        changeAdminEditDataFieldSelected: (text) => dispatch(changeAdminEditDataFieldSelected(text)),
        changeAdminEditDataValueSelected: (text) => dispatch(changeAdminEditDataValueSelected(text)),
        addActivity: (data) => dispatch(addActivity(data))
    }
}

class EditForm extends Component {

    handleSubmit = () => {
        if (this.props.adminEditValue === '') {
            window.alert('Please select a value you would like to edit to.')
        }
        else if (this.props.adminEditField === 'date' && !isValidDate(this.props.adminEditValue)) {
            return window.alert('Please input the date correctly as mm/dd/YYYY')
        }
        else if (this.props.adminEditField === 'code' && !isValidCode(this.props.adminEditValue)) {
            return window.alert('Please write the code in the correct format')
        }
        else {

            const findIndex = () => {
                for (let i = 0; i < this.props.info.length; i++) {
                    if (this.props.info[i].key === this.props.selectedRowAdminDocRepo) {
                        return i;
                    }
                }
            }

            this.props.addActivity({
                activity: 'Edited Document: ' + this.props.info[findIndex()].code,
                user: this.props.name,
                date: getCurrentDateTime(),
                division: this.props.dept,
                ip: '111.111.111'
            })

            let newData = this.props.info[findIndex()];

            switch (this.props.adminEditField) {
                case 'type':
                    newData.type = this.props.adminEditValue
                    this.props.editData({
                        newData
                    })
                    break;
                case 'market':
                    newData.market = this.props.adminEditValue
                    this.props.editData({
                        newData
                    })
                    break;
                case 'language':
                    newData.language = this.props.adminEditValue
                    this.props.editData({
                        newData
                    })
                    break;
                case 'visibility':
                    newData.visibility = this.props.adminEditValue
                    this.props.editData({
                        newData
                    })
                    break;
                case 'date':
                    newData.date = this.props.adminEditValue
                    this.props.editData({
                        newData
                    })
                    break;
                case 'code':
                    newData.code = this.props.adminEditValue
                    this.props.editData({
                        newData
                    })
                    break;
                case 'status':
                    newData.status = this.props.adminEditValue
                    this.props.editData({
                        newData
                    })
                    break;
                case 'name':
                    newData.name = this.props.adminEditValue
                    this.props.editData({
                        newData
                    })
                    break;
                default:
                    break;
            }
            this.props.changeAdminEditDataFieldSelected('')
            this.props.changeAdminEditDataValueSelected('')
            this.props.setRowSelectedAdminDocRepo('')
            this.props.setAdminEditDocRepoStatus(false)
        }
    }

    handleValueChange = (event) => {
        this.props.changeAdminEditDataValueSelected(event.target.value)
    }

    handleClick = (event) => {
        this.props.changeAdminEditDataFieldSelected(event.target.value)
    }

    renderEditInput = () => {
        if (this.props.adminEditField === 'type') {
            return (
                <div>
                    <select style={{ width: "50%", fontSize: '0.8vw', marginLeft: '25%' }} onChange={this.handleValueChange}>
                        <option selected='true' disabled>Change to:</option>
                        {this.props.typeOptions.map(typeOption => (
                            <option key={typeOption.value} value={typeOption.text}>
                                {typeOption.text}
                            </option>
                        ))}
                    </select>
                    <Button size='small' variant="outlined" style={{ width: '50%', fontSize: '0.8vw' }} onClick={() => { this.handleSubmit() }}>
                        Submit
                    </Button>
                </div>
            )
        }
        else if (this.props.adminEditField === 'market') {
            return (
                <div>
                    <select style={{ width: "50%", fontSize: '0.8vw', marginLeft: '25%' }} onChange={this.handleValueChange}>
                        <option selected='true' disabled>Change to:</option>
                        {this.props.marketOptions.map(marketOption => (
                            <option key={marketOption.text} value={marketOption.value}>
                                {marketOption.text}
                            </option>
                        ))}
                    </select>
                    <Button size='small' variant="outlined" style={{ width: '50%', fontSize: '0.8vw' }} onClick={() => { this.handleSubmit() }}>
                        Submit
                    </Button>
                </div>
            )
        }
        else if (this.props.adminEditField === 'language') {
            return (
                <div>
                    <select style={{ width: "50%", fontSize: '0.8vw', marginLeft: '25%' }} onChange={this.handleValueChange}>
                        <option selected='true' disabled>Change to:</option>
                        {this.props.languageOptions.map(languageOption => (
                            <option key={languageOption.text} value={languageOption.value}>
                                {languageOption.text}
                            </option>
                        ))}
                    </select>
                    <Button size='small' variant="outlined" style={{ width: '50%', fontSize: '0.8vw' }} onClick={() => { this.handleSubmit() }}>
                        Submit
                    </Button>
                </div>
            )
        }
        else if (this.props.adminEditField === 'date') {
            return (
                <div>
                    <input style={{ width: "50%", fontSize: '0.8vw', marginLeft: '25%' }} id='txt' type='text' onChange={this.handleValueChange}></input>
                    <Button size='small' variant="outlined" style={{ width: '50%', fontSize: '0.8vw' }} onClick={() => { this.handleSubmit() }}>
                        Submit
                    </Button>
                </div>
            )
        }
        else if (this.props.adminEditField === 'visibility') {
            return (
                <div>
                    <select style={{ width: "50%", fontSize: '0.8vw', marginLeft: '25%' }} onChange={this.handleValueChange}>
                        <option selected='true' disabled>Change to:</option>
                        <option value="PUBLIC">PUBLIC</option>
                        <option value="TENANTID">TENANTID</option>
                    </select>
                    <Button size='small' variant="outlined" style={{ width: '50%', fontSize: '0.8vw' }} onClick={() => { this.handleSubmit() }}>
                        Submit
                    </Button>
                </div>
            )
        }
        else if (this.props.adminEditField === 'code') {
            return (
                <div>
                    <input style={{ width: "50%", fontSize: '0.8vw', marginLeft: '25%' }} id='txt' type='text' onChange={this.handleValueChange}></input>
                    <Button size='small' variant="outlined" style={{ width: '50%', fontSize: '0.8vw' }} onClick={() => { this.handleSubmit() }}>
                        Submit
                    </Button>
                </div>
            )
        }
        else if (this.props.adminEditField === 'status') {
            return (
                <div>
                    <select style={{ width: "50%", fontSize: '0.8vw', marginLeft: '25%' }} onChange={this.handleValueChange}>
                        <option selected='true' disabled>Change to:</option>
                        <option value="live">Live</option>
                        <option value="pending">Pending</option>
                        <option value="expired">Expired</option>
                    </select>
                    <Button size='small' variant="outlined" style={{ width: '50%', fontSize: '0.8vw' }} onClick={() => { this.handleSubmit() }}>
                        Submit
                    </Button>
                </div>
            )
        }
        else if (this.props.adminEditField === 'name') {
            return (
                <div>
                    <input style={{ width: "50%", fontSize: '0.8vw', marginLeft: '25%' }} id='txt' type='text' onChange={this.handleValueChange}></input>
                    <Button size='small' variant="outlined" style={{ width: '50%', fontSize: '0.8vw' }} onClick={() => { this.handleSubmit() }}>
                        Submit
                    </Button>
                </div>
            )
        }
        else {
            return null;
        }
    }

    render() {
        if (this.props.selectedRowAdminDocRepo !== '') {
            return (
                <div style={{ textAlign: 'center' }}>
                    <select style={{ fontSize: '0.8vw', width: '50%', display: 'inline-block' }} onChange={this.handleClick}>
                        <option selected='true' disabled>Select Field:</option>
                        <option value='code'>Code</option>
                        <option value='visibility'>Visibility</option>
                        <option value='market'>Market</option>
                        <option value='type'>Type</option>
                        <option value='language'>Language</option>
                        <option value='date'>Date</option>
                        <option value='status'>Status</option>
                        <option value='name'>Name</option>
                    </select>
                    {this.renderEditInput()}
                </div>
            )
        }
        else {
            return null;
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(EditForm)
