import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import { connect } from 'react-redux';
import {
    setRowSelectedAdminDocRepo,
    setAdminEditDocRepoStatus,
    changeAdminEditDataFieldSelected,
    changeAdminEditDataValueSelected
} from '../../actions';

const mapStateToProps = state => {
    return {
        adminEditDocumentRepoStatus: state.adminEditDocumentRepoStatusData.adminEditDocumentRepoStatus,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        setRowSelectedAdminDocRepo: (key) => dispatch(setRowSelectedAdminDocRepo(key)),
        setAdminEditDocRepoStatus: (bool) => dispatch(setAdminEditDocRepoStatus(bool)),
        changeAdminEditDataFieldSelected: (text) => dispatch(changeAdminEditDataFieldSelected(text)),
        changeAdminEditDataValueSelected: (text) => dispatch(changeAdminEditDataValueSelected(text))
    }
}

class EditButton extends Component {
    button_color = function (status) {
        switch (status) {
            case true:
                return 'secondary'
            case false:
                return 'black'
            default:
                return 'black'
        }
    }
    handleClick = () => {
        if (this.props.adminEditDocumentRepoStatus === true) {
            this.props.setAdminEditDocRepoStatus(false)
            this.props.changeAdminEditDataFieldSelected('')
            this.props.changeAdminEditDataValueSelected('')
            this.props.setRowSelectedAdminDocRepo('')
        }
        else {
            this.props.setAdminEditDocRepoStatus(true)
        }
    }
    render() {
        return (
            <div style={{ paddingRight: '1rem' }}>
                <Button style={{ paddingRight: '1rem', fontSize: '0.6vw' }}
                    color={this.button_color(this.props.adminEditDocumentRepoStatus)}
                    variant="outlined"
                    onClick={() => { this.handleClick() }}>
                    Edit
                        </Button>
            </div>
        )
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(EditButton);
