import React, { Component } from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const smallTable = [
    {
        key: 0,
        val: "Market",
    },
    {
        key: 1,
        val: "Type",
    },
    {
        key: 2,
        val: "Language",
    },
];

class DataTable extends Component {

    handleChange = (key) => {
        if (this.props.selectedFieldRow !== key) {
            this.props.setFieldRowSelected(key)
        } else {
            this.props.setFieldRowSelected('')
        }
        this.props.setEditorRowSelected('')
    }

    bgColor = (key) => {
        if (this.props.selectedFieldRow === key) {
            return '#d1d1d1';
        } else {
            return 'white';
        }
    }

    render() {
        return (
            <div>
                <div style={{ overflow: "auto" }}>
                    <Paper>
                        <div style={{ overflow: "auto", height: '200px' }}>
                            <Table className='border' style={{ tableLayout: "fixed" }}>
                                <TableBody>
                                    <TableRow style={{ backgroundColor: '#800000' }}>
                                        <TableCell style={{ fontSize: '7rem', color: 'white' }} align="center" component="th" scope="row">
                                            SELECT
                                        </TableCell>
                                    </TableRow>
                                </TableBody>
                            </Table>
                        </div>
                        <div style={{ overflow: "auto", height: '572px' }}>
                            <Table className='border' style={{ tableLayout: "fixed" }}>
                                <TableBody>
                                    {smallTable.map(row => (
                                        <TableRow key={row.key} onClick={event => this.handleChange(row.key)} style={{ backgroundColor: this.bgColor(row.key) }}>
                                            <TableCell style={{ fontSize: '7rem' }} align="center" component="th" scope="row">
                                                {row.val}
                                            </TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </div>
                    </Paper>
                </div>
            </div>
        );
    }
}

export default DataTable;
