import {
    ADD_NEW_TASK,
    TEST_STATE_CHANGE
} from './constants';

import {tasks} from './Data/tasks'

//EditTasks
const initialStateTasks = {
    tasksVar: tasks
}

export const editTasks = (state = initialStateTasks, action = {}) => {
    switch (action.type) {
        case ADD_NEW_TASK:
            return Object.assign({},
                state,
                { tasksVar: [...state.tasksVar, action.payload] });
        case TEST_STATE_CHANGE:
            return Object.assign({},
                state,
                { tasksVar:action.payload});
        default:
            return state;
    }
}
