import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Circle } from 'react-shapes';

const mapStateToProps = state => {
    return {
        tasks: state.editTasks.tasksVar,
        tasksLength: state.editTasks.tasksLength
    }
}

class Visualisation extends Component {

    render() {

        const tasksList = [];

        for (let i = 0; i < this.props.tasksLength; i++) {
            {console.log(this.props.tasks[i].subtasks.length*40)}
            tasksList.push(
                <div>
                    <h1 style = {{marginLeft: this.props.tasks[i].subtasks.length*20 }}>{this.props.tasks[i].name}</h1>
                    <Circle r={this.props.tasks[i].subtasks.length*40} fill={{ color: '#2409ba' }} /><br />
                </div>
            )
        }

        return (
            <div>
                {tasksList}
            </div>
        )
    }
}

export default connect(mapStateToProps)(Visualisation)
