import React, { Component } from "react";
import { connect } from 'react-redux'
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import NavDrawer from "../../NavDrawer";
import SearchBox from "./SearchBox";
import StatusButtons from './StatusButtons';
import Sort from './Sort';
import UploadButton from './UploadButton';
import EditButton from './EditButton';
import DeleteButton from './DeleteButton'
import 'tachyons';

const mapStateToProps = state => {
    return {
        userEditDocumentRepoStatus: state.userEditDocumentRepoStatusData.userEditDocumentRepoStatus,
    }
}

class NavBar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            drawerOpened: false
        };
    }

    renderDeleteButton = () => {
        if (this.props.userEditDocumentRepoStatus===true){
            return (
                <DeleteButton/>
            )
        } else {
            return null;
        }
    }
    toggleDrawer = booleanValue => () => {
        this.setState({
            drawerOpened: booleanValue
        });
    };
    render() {
        return (
            <div className="App" style={{ fontSize: '0.6vw' }}>
                <AppBar position="static">
                    <Toolbar style={{ backgroundColor: '#3830ab', paddingLeft: '0px' }}>
                        <IconButton
                            color="inherit"
                            aria-label="Menu"
                            onClick={this.toggleDrawer(true)}
                        >
                            <MenuIcon />
                        </IconButton>
                        <Typography className='pr3' style={{ fontSize: '1.4vw' }} color="inherit">
                            DOCUMENT REPOSITORY
                        </Typography>
                        <SearchBox />
                        <StatusButtons />
                        <Sort className='f1' />
                        <UploadButton />
                        <EditButton />
                        {this.renderDeleteButton()}
                    </Toolbar>
                </AppBar>
                <NavDrawer
                    drawerOpened={this.state.drawerOpened}
                    toggleDrawer={this.toggleDrawer}
                    console='user'
                />
            </div>
        );
    }
}

export default connect(mapStateToProps)(NavBar)
