import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import {
    setSearchField,
    setSort,
    setStatus,
    setRowSelectedUserDocRepo,
    setUserEditDocRepoStatus,
    editData,
    changeUserEditDataFieldSelected,
    changeUserEditDataValueSelected
} from '../../actions';


const mapDispatchToProps = (dispatch) => {
    return {
        onSearchChange: (event) => dispatch(setSearchField(event.target.value)),
        setSort: (event) => dispatch(setSort(event.target.value)),
        setStatus: (text) => dispatch(setStatus(text)),
        setRowSelectedUserDocRepo: (key) => dispatch(setRowSelectedUserDocRepo(key)),
        setUserEditDocRepoStatus: (bool) => dispatch(setUserEditDocRepoStatus(bool)),
        editData: (data) => dispatch(editData(data)),
        changeUserEditDataFieldSelected: (text) => dispatch(changeUserEditDataFieldSelected(text)),
        changeUserEditDataValueSelected: (text) => dispatch(changeUserEditDataValueSelected(text))
    }
}

class UploadButton extends Component {

    handleClick = () => {
        this.props.setUserEditDocRepoStatus(false)
        this.props.changeUserEditDataFieldSelected('')
        this.props.changeUserEditDataValueSelected('')
        this.props.setRowSelectedUserDocRepo('')
    }

    render() {
        return (
            <div style = {{paddingRight: '1rem'}}>
                <Button size='small'
                    variant="outlined"
                    style={{ fontSize: '0.6vw' }}
                    component={Link} to="/User/UserUpload"
                    onClick={() => { this.handleClick() }}>
                    Upload      </Button>
            </div>
        );
    }
}

export default connect(null,mapDispatchToProps)(UploadButton)
