import React, { Component } from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const accessColor = (access) => {
  if (access === 'granted') {
    return 'green';
  }
  if (access === 'revoked') {
    return 'red';
  }
}

const tableHeight = (lengthOfArray) => {
  if (lengthOfArray * 35 <= 1000) {
    var num = lengthOfArray * 35;
    return num.toString() + 'px';
  }
  else {
    return '1000px';
  }
}

export default class DataTable extends Component {

  render() {
    return (
      <div style={{ overflow: "auto", paddingRight: '15px' }}>
        <Paper>
          <Table>
            <TableHead>
              <TableRow
                style={{
                  backgroundColor: "#f5f5f5",
                  height: '2vw'
                }}
              >
                <TableCell style={{ fontSize: '1.2vw' }} >User</TableCell>
                <TableCell style={{ fontSize: '1.2vw' }} align='right'>Role/Department</TableCell>
                <TableCell style={{ fontSize: '1.2vw' }} align="right">Access</TableCell>
              </TableRow>
            </TableHead>
          </Table>
          <div style={{ overflow: "auto", height: tableHeight(this.props.info.length) }}>
            <Table style={{ tableLayout: "fixed" }}>
              <TableBody>
                {this.props.info.map(row => (
                  <TableRow >
                    <TableCell style={{ fontSize: '0.8vw' }} component="th" scope="row">
                      {row.name}
                    </TableCell>
                    <TableCell style={{ fontSize: '0.8vw' }} align="right">{row.role}</TableCell>
                    <TableCell style={{ color: accessColor(row.access.toLowerCase()), fontSize: '0.8vw' }} align="right">{row.access}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
        </Paper>
      </div>
    );
  }
}
