import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Circle } from 'react-shapes';

const mapStateToProps = state => {
    return {
        tasks: state.editTasks.tasksVar
    }
}

class Visualisation extends Component {

    constructor() {
        super()
        this.state = {
            tasks: []
        }
    }

    render() {

        const tasksList = [];

        for (let i = 1; i <= this.props.tasks.length; i++) {
            tasksList.push(
                <div>
                    <Circle r={50} fill={{ color: '#2409ba' }} stroke={{ color: '#E65243' }} strokeWidth={3} /><br />
                </div>
            )
        }

        return (
            <div>
                {tasksList}
            </div>
        )
    }
}

export default connect(mapStateToProps)(Visualisation)
