import React, { Component } from 'react';
import { connect } from 'react-redux';
import Button from '@material-ui/core/Button';
import {setStatus} from '../../actions';

const mapStateToProps = state => {
    return {
        status: state.userDocumentRepoStatusData.userDocumentRepoStatus,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        setStatus: (text) => dispatch(setStatus(text)),
    }
}

class StatusButtons extends Component {
    button_color = function (status) {
        switch (status) {
            case "pending":
                return {
                    pending: "secondary",
                    live: "black",
                    expired: "black",
                    all: "black"
                };
            case "live":
                return {
                    pending: "black",
                    live: "secondary",
                    expired: "black",
                    all: "black"
                };
            case "expired":
                return {
                    pending: "black",
                    live: "black",
                    expired: "secondary",
                    all: "black"
                };
            case "all":
                return {
                    pending: "black",
                    live: "black",
                    expired: "black",
                    all: "secondary"
                }
            default:
                return {
                    pending: "black",
                    live: "black",
                    expired: "black",
                    all: "secondary"
                }
        }
    }
    render() {
        return (
            <div>
                <Button style={{ paddingLeft: '1rem', fontSize: '0.6vw' }} color={this.button_color(this.props.status).all} onClick={() => { this.props.setStatus("all") }}>
                    All
                        </Button>
                <Button style={{ fontSize: '0.6vw' }} color={this.button_color(this.props.status).pending} onClick={() => { this.props.setStatus("pending") }}>
                    Pending
                        </Button>
                <Button style={{ fontSize: '0.6vw' }} color={this.button_color(this.props.status).live} onClick={() => { this.props.setStatus("live") }}>
                    Live
                        </Button>
                <Button style={{ paddingRight: '1rem', fontSize: '0.6vw' }} color={this.button_color(this.props.status).expired} onClick={() => { this.props.setStatus("expired") }}>
                    Expired
                        </Button>
            </div>
        )
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(StatusButtons);
