import React, { Component } from 'react';
import { connect } from 'react-redux';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { setRowNotSelected, setRowSelected } from '../../actions'

const mapStateToProps = state => {
    return {
        deptFilter: state.adminAccessControlDeptFilterData.adminAccessControlDeptFilter,
        adminAccessControlSelectedRows: state.adminAccessControlSelectedRowsData.adminAccessControlSelectedRows,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        setRowSelected: (key) => dispatch(setRowSelected(key)),
        setRowNotSelected: (key) => dispatch(setRowNotSelected(key)),
    }
}

const tableHeight = (lengthOfArray) => {
    if (lengthOfArray * 35 <= 300) {
        var num = lengthOfArray * 35;
        return num.toString() + 'px';
    }
    else {
        return '300px';
    }
}

class DataTable extends Component {

    handleChange = (key) => {
        if (this.props.adminAccessControlSelectedRows.includes(key)) {
            this.props.setRowSelected(key);
        } else {
            this.props.setRowNotSelected(key);
        }

    }

    bgColor = (key) => {
        if (this.props.adminAccessControlSelectedRows.includes(key)) {
            return '#d1d1d1';
        } else {
            return 'white';
        }
    }

    render() {
        let filteredInfo = this.props.info.filter(info => {
            if (this.props.deptFilter === "none") {
                return info;
            } else {
                return info.dept === this.props.deptFilter
            }
        })

        return (
            <div>
                <div style={{ overflow: "auto" }}>
                    <Paper>
                        <Table>
                            <TableHead>
                                <TableRow
                                    style={{
                                        backgroundColor: "#f5f5f5",
                                        height: "35px"
                                    }}
                                >
                                    <TableCell style={{ fontSize: '0.8vw' }} >User</TableCell>
                                    <TableCell style={{ fontSize: '0.8vw' }} >Role/Department</TableCell>
                                    <TableCell />
                                </TableRow>
                            </TableHead>
                        </Table>
                        <div style={{ overflow: "auto", height: tableHeight(filteredInfo.length) }}>
                            <Table style={{ tableLayout: "fixed" }}>
                                <TableBody>
                                    {filteredInfo.map(row => (
                                        <TableRow key={row.user} onClick={event => this.handleChange(row.user)} style={{ backgroundColor: this.bgColor(row.user) }}>
                                            <TableCell style={{ fontSize: '0.5vw' }} component="th" scope="row">
                                                {row.name}
                                            </TableCell>
                                            <TableCell style={{ fontSize: '0.5vw' }} align="right">{row.role}</TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </div>
                    </Paper>
                </div>
            </div>
        );
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(DataTable);
