import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Rectangle, Circle, Ellipse, Line, Polyline, CornerBox, Triangle } from 'react-shapes';

const mapStateToProps = state => {
    console.log('iterative visualisation receives new props from redux state')
    return {
        tasks: state.editTasks.tasksVar
    }
}

class Visualisation extends Component {

    constructor() {
        super()
        this.state = {
            tasks: []
        }
    }

    iterativeTaskVisualiation = () => {
        for (let i = 0; i < this.props.tasks.length; i++) {
            return (
                <div>
                    <Circle r={50} fill={{ color: '#2409ba' }} stroke={{ color: '#E65243' }} strokeWidth={3} /><br/>
                </div>
            )
        }
    }

    componentDidUpdate(prevProps){
        if (prevProps.tasks!==this.props.tasks){
            console.log('newProps')
            this.setState({tasks:this.props.tasks})
        }
    }

    render() {
        return (
            <div>
                {this.iterativeTaskVisualiation()}
            </div>
        )
    }
}

export default connect(mapStateToProps)(Visualisation)
