import React, { Component } from "react";
import { connect } from 'react-redux'
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import '../index.css'
import {
  displayANTBoolFalse,
  displayANTBoolTrue
} from '../actions'

const mapStateToProps = state => {
    return {
      displayANTBool: state.displayANTBool.displayANTBoolVal
    }
}

const mapDispatchToProps = (dispatch) => {
  return {
    displayANTBoolTrue: (bool) => dispatch(displayANTBoolTrue(bool)),
    displayANTBoolFalse: (bool) => dispatch(displayANTBoolFalse(bool))
  }
}

function toggleANTBool() {
  if (displayANTBool == false){
    displayANTBoolTrue()
  }
  else {
    displayANTBoolFalse()
  }
}

class AddTaskButton extends Component {
    constructor(props) {
        super(props);
        this.state = {
        };
    }
    render() {
        return (
            <div className="App">
                <button className='AddTaskButton' onClick = {this.toggleANTBool}>Add Task</button>
            </div>
        );
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(AddTaskButton)
