import React, { Component } from 'react';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import 'bootstrap/dist/css/bootstrap.css';
import NavBar from './NavBar';
import Table from './Table';
import Sidebar from './Sidebar';
import { connect } from 'react-redux';

const mapStateToProps = state => {
    return {
        adminAccessControlSelectedRows: state.adminAccessControlSelectedRowsData.adminAccessControlSelectedRows,
        info: state.adminAccessControlInfoData.adminAccessControlInfo
    }
}

class AccessControl extends Component {
    constructor() {
        super();
        this.state = { navBarHeight: 0 };
        this.updateHeight = this.updateHeight.bind(this);
    }

    componentDidMount() {
        this.updateHeight();
        window.addEventListener("resize", this.updateHeight);
    }

    componentWillUnmount() {
        window.removeEventListener("resize", this.updateHeight);
    }

    componentDidUpdate() {
        this.updateHeight();
    }

    updateHeight() {
        if (this.state.navBarHeight !== this.div.clientHeight)
            this.setState({ navBarHeight: this.div.clientHeight })
    }

    render() {
        const navBarHeightString = this.state.navBarHeight + 'px'
        return (
            <Container fluid={true} style={{ paddingLeft: '0px' }} >
                <Row>
                    <Col md={2} style={{ backgroundColor: '#800000' }}>
                        <div ref={div => { this.div = div; }}>
                            <NavBar></NavBar>                </div>
                        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: `calc(100vh - ${navBarHeightString})`, marginLeft: '15px' }}>
                            <Sidebar info={this.props.info}></Sidebar>
                        </div>
                    </Col>
                    <Col md={10} style={{ backgroundColor: '#800000' }}>
                        <Row style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
                            <Table info={this.props.info}></Table>
                        </Row>
                    </Col>
                </Row>
            </Container >
        )
    }
}

export default connect(mapStateToProps)(AccessControl);
