import React, { Component } from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { connect } from 'react-redux';
import { setRowSelectedUserDocRepo, } from '../../actions';

const mapStateToProps = state => {
  return {
    searchfield: state.userDocumentRepoSearchData.userDocumentRepoSearchField,
    sort: state.userDocumentRepoSortData.userDocumentRepoSort,
    status: state.userDocumentRepoStatusData.userDocumentRepoStatus,
    info: state.documentRepoInfoData.documentRepoInfoDataVar,
    userEditDocumentRepoStatus: state.userEditDocumentRepoStatusData.userEditDocumentRepoStatus,
    selectedRowUserDocRepo: state.selectedRowUserDocRepoData.selectedRowUserDocRepo,
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    setRowSelectedUserDocRepo: (key) => dispatch(setRowSelectedUserDocRepo(key)),
  }
}

class SimpleTable extends Component {

  handleChange = (key) => {
    if (this.props.userEditDocumentRepoStatus === true) {
      this.props.setRowSelectedUserDocRepo(key)
    }
  }

  bgColor = (key) => {
    if (this.props.userEditDocumentRepoStatus === true) {
      if (this.props.selectedRowUserDocRepo === key) {
        return '#d1d1d1';
      } else {
        return 'white';
      }
    }
    else {
      return 'white';
    }
  }

  render() {

    let firstFilter = this.props.info.filter(info => {
      return (
        info.code.toLowerCase().includes(this.props.searchfield.toLowerCase()) ||
        info.visibility.toLowerCase().includes(this.props.searchfield.toLowerCase()) ||
        info.market.toLowerCase().includes(this.props.searchfield.toLowerCase()) ||
        info.type.toLowerCase().includes(this.props.searchfield.toLowerCase()) ||
        info.language.toLowerCase().includes(this.props.searchfield.toLowerCase()) ||
        info.date.toLowerCase().includes(this.props.searchfield.toLowerCase()) ||
        info.name.toLowerCase().includes(this.props.searchfield.toLowerCase()));
    })

    let filteredInfo = firstFilter.filter(firstFilter => {
      if (this.props.status === "all") {
        return firstFilter;
      } else {
        return firstFilter.status === this.props.status
      }
    })

    const finalFilter = (sort) => {
      switch (sort) {
        case "date":
          filteredInfo.sort(function (a, b) {
            var dateA = new Date(a.date), dateB = new Date(b.date)
            return dateA - dateB;
          })
          break;
        case "code":
          filteredInfo.sort(function (a, b) {
            var codeA = a.code.toLowerCase(), codeB = b.code.toLowerCase()
            if (codeA < codeB) {
              return -1
            }
            if (codeB < codeA) {
              return 1
            }
            return 0
          })
          break;
        case "visibility":
          filteredInfo.sort(function (a, b) {
            var visibilityA = a.visibility.toLowerCase(), visibilityB = b.visibility.toLowerCase()
            if (visibilityA < visibilityB) {
              return -1
            }
            if (visibilityB < visibilityA) {
              return 1
            }
            return 0
          })
          break;
        case "market":
          filteredInfo.sort(function (a, b) {
            var marketA = a.market.toLowerCase(), marketB = b.market.toLowerCase()
            if (marketA < marketB) {
              return -1
            }
            if (marketB < marketA) {
              return 1
            }
            return 0
          })
          break;
        case "type":
          filteredInfo.sort(function (a, b) {
            var typeA = a.type.toLowerCase(), typeB = b.type.toLowerCase()
            if (typeA < typeB) {
              return -1
            }
            if (typeB < typeA) {
              return 1
            }
            return 0
          })
          break;
        case "language":
          filteredInfo.sort(function (a, b) {
            var languageA = a.language.toLowerCase(), languageB = b.language.toLowerCase()
            if (languageA < languageB) {
              return -1
            }
            if (languageB < languageA) {
              return 1
            }
            return 0
          })
          break;
        case "name":
          filteredInfo.sort(function (a, b) {
            var nameA = a.name.toLowerCase(), nameB = b.name.toLowerCase()
            if (nameA < nameB) {
              return -1
            }
            if (nameB < nameA) {
              return 1
            }
            return 0
          })
          break;
        default:
          break;
      }
    }
    return (
      <Paper style={{ marginTop: '0px' }} >
        <Table >
          <TableHead>
            <TableRow style={{ height: '2.5vw' }}>
              <TableCell style={{ fontSize: '1.5vw' }} >Code</TableCell>
              <TableCell style={{ fontSize: '1.5vw' }} align="right">Name</TableCell>
              <TableCell style={{ fontSize: '1.5vw' }} align="right">Visibility</TableCell>
              <TableCell style={{ fontSize: '1.5vw' }} align="right">Market</TableCell>
              <TableCell style={{ fontSize: '1.5vw' }} align="right">Type</TableCell>
              <TableCell style={{ fontSize: '1.5vw' }} align="right">Language</TableCell>
              <TableCell style={{ fontSize: '1.5vw' }} align="right">Date</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {finalFilter(this.props.sort)}
            {filteredInfo.map(row => (
              <TableRow key={row.key} onClick={event => this.handleChange(row.key)} style={{ backgroundColor: this.bgColor(row.key) }}>
                <TableCell component="th" scope="row" style={{ fontSize: '0.8vw' }}>
                  {row.code}
                </TableCell>
                <TableCell style={{ fontSize: '0.8vw' }} align="right">{row.name}</TableCell>
                <TableCell style={{ fontSize: '0.8vw' }} align="right">{row.visibility}</TableCell>
                <TableCell style={{ fontSize: '0.8vw' }} align="right">{row.market}</TableCell>
                <TableCell style={{ fontSize: '0.8vw' }} align="right">{row.type}</TableCell>
                <TableCell style={{ fontSize: '0.8vw' }} align="right">{row.language}</TableCell>
                <TableCell style={{ fontSize: '0.8vw' }} align="right">{row.date}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(SimpleTable)
