import React, { Component } from 'react';
import { connect } from 'react-redux';
import SimpleTable from './Table';
import './DocumentRepo.css'
import NavBar from './NavBar';
import EditForm from './EditForm'

const mapStateToProps = state => {
    return {
        userEditDocumentRepoStatus: state.userEditDocumentRepoStatusData.userEditDocumentRepoStatus,
    }
}

class DocumentRepo extends Component {

    editForm = () => {
        if (this.props.userEditDocumentRepoStatus === true) {
            return (
                <div >
                    <EditForm />
                </div>
            )
        } else {
            return null;
        }
    }

    render() {
        return (
            <div>
                <NavBar />
                <SimpleTable />
                {this.editForm()}
            </div>
        );
    }
}

export default connect(mapStateToProps)(DocumentRepo);
