import React, { Component } from 'react';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import FieldTable from './FieldTable'
import ActionTable from './ActionTable'
import EditorTable from './EditorTable'
import ActionButtons from './ActionButtons'
import 'bootstrap/dist/css/bootstrap.css';

class EditValues extends Component {
    constructor() {
        super()
        this.state = {
            selectedFieldRow: '',
            selectedActionRow: '',
            selectedEditorRow: ''
        }
    }

    setFieldRowSelected = (row) => {
        this.setState(
            { selectedFieldRow: row }
        )
    }

    setActionRowSelected = (row) => {
        this.setState(
            { selectedActionRow: row }
        )
    }

    setEditorRowSelected = (row) => {
        this.setState(
            { selectedEditorRow: row }
        )
    }

    render() {

        const navBarHeightString = this.props.navBarHeight + 'px'

        return (
            <div>
                <Container fluid={true} style={{ paddingLeft: '0px' }} >
                    <Row style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: `calc(100vh - ${navBarHeightString})` }}>
                        <Col md={4}>
                            <Row >
                                <FieldTable selectedFieldRow={this.state.selectedFieldRow}
                                    setFieldRowSelected={this.setFieldRowSelected}
                                    setEditorRowSelected={this.setEditorRowSelected}></FieldTable>
                            </Row>
                        </Col>
                        <Col md={4}>
                            <ActionTable selectedActionRow={this.state.selectedActionRow}
                                setActionRowSelected={this.setActionRowSelected}
                            ></ActionTable>
                            <ActionButtons selectedActionRow={this.state.selectedActionRow}
                                selectedEditorRow={this.state.selectedEditorRow}
                                selectedFieldRow={this.state.selectedFieldRow}
                                setActionRowSelected={this.setActionRowSelected}
                                setEditorRowSelected={this.setEditorRowSelected}
                                setFieldRowSelected={this.setFieldRowSelected}>
                            </ActionButtons>
                        </Col>
                        <Col md={4}>
                            <Row >
                                <EditorTable selectedEditorRow={this.state.selectedEditorRow}
                                    setEditorRowSelected={this.setEditorRowSelected}
                                    selectedFieldRow={this.state.selectedFieldRow}
                                    selectedActionRow={this.state.selectedActionRow}>
                                </EditorTable>
                            </Row>
                        </Col>
                    </Row>
                </Container >
            </div>
        )
    }
}

export default EditValues;
