import {
    ADD_NEW_TASK,
    DISPLAY_ANT_BOOL_TRUE,
    DISPLAY_ANT_BOOL_FALSE
} from './constants';

import { tasks } from './Data/tasks'

//EditTasks
const initialStateTasks = {
    tasksVar: tasks
}

export const editTasks = (state = initialStateTasks, action = {}) => {
    switch (action.type) {
        case ADD_NEW_TASK:
            return Object.assign({},
                state,
                { tasksVar: [...state.tasksVar, action.payload] });
        default:
            return state;
    }
}

//Add Task Bool 
const initialStateANTBool = {
    ANTBool: false
}

export const displayANTState = (state = initialStateANTBool, action = {}) => {
    switch (action.type) {
        case DISPLAY_ANT_BOOL_TRUE:
            return Object.assign({},
                state,
                { ANTBool: true });
        case DISPLAY_ANT_BOOL_FALSE:
            return Object.assign({},
                state,
                { ANTBool: false });
        default:
            return state;
    }
}
